import React, { useState } from 'react';

import Tasks from './components/Tasks';
import './App.css';
import AddTask from './components/AddTask';

const App = () => {
  const [tasks, setTasks] = useState([
    {
      id: '1',
      title: 'Estudar Front-End',
      completed: false,
    },
    {
      id: '2',
      title: 'Arrumar a Casa',
      completed: true,
    },
    {
      id: '2',
      title: 'Lavar o carro na oficina',
      completed: true,
    }
  ]);

  const handleTaskAddition = (taskTitle) => {
    const newTasks = [
      ... tasks, {
      title: taskTitle,
      id: Math.random(10),
      completed: false
    }];

    setTasks(newTasks);
  }

  return (
    <>
      <div className='container'>
        <AddTask />
        <Tasks tasks={tasks} />
      </div>
    </>
  );
};

export default App;
